#!/usr/bin/env python3# -*- coding: utf-8 -*-import osfrom datetime import datetimefrom importlib import reloadfrom bisect import bisectfrom itertools import zip_longest# import matplotlib.gridspec as gridspec# import matplotlib.patches as patchesimport matplotlib.pyplot as pltimport numpy as npimport pandas as pd#from mpl_toolkits.axes_grid1.inset_locator import inset_axes# from matplotlib import markers# from matplotlib.patches import Rectangle# from pandas.plotting import tableimport config# import fig_proposal as figp# import general_functions as gfunc# import load.load_data as ldat# import load.load_traces as ltra# import old.old_figs as ofig# import itertools# nb description with pandas:# pd.options.display.max_columns = 30#===========================# global setup# NB fig size : 8.5, 11.6 or 17.6 cmanot = True           # to draw the date and name on the bottom of the plotstd_colors = config.std_colors()speed_colors = config.speed_colors()plt.rcParams.update(config.rc_params())paths = config.build_paths()os.chdir(paths['pg'])paths['data'] = os.path.join(paths['owncFig'], 'data')def load_speed_data():    file = 'baudot.csv'    filename = os.path.join(paths['data'], file)    bddf = pd.read_csv(filename)    bddf.columns = [_.strip() for _ in bddf.columns]    file = 'neuron_props_speed.xlsx'    filename = os.path.join(paths['data'], 'averageTraces', file)    cgdf = pd.read_excel(filename)    cols = [st.strip() for st in cgdf.columns]    cols = [st.lower() for st in cols]    cols = [st.replace('(', '_') for st in cols]    cols = [st.replace(')', '') for st in cols]    cgdf.columns = cols    return bddf, cgdfdef build_summary(bddf, cgdf):    """    class the number of cells    input = baudot dataframe & centrigabor dataframe    output = summary dataframe (nb of cells, upper interval spped limit)    """    # count number of cells    def class_speed(s):        "return upper limit in class"        lims = range(100, 500, 25)        i = bisect(lims, s)        return(lims[i])    cgdf['optiMax'] = cgdf.speed_isi0.apply(lambda x: class_speed(x))    cgNumb = dict(cgdf.optiMax.value_counts())    bdNumb = dict(bddf.set_index('optiMax'))    summary_df = pd.DataFrame(index=range(100, 525, 25))    summary_df = summary_df.join(pd.DataFrame(bdNumb))    summary_df.columns = ['bd_cells']    summary_df['cg_cells'] = pd.Series(cgNumb)    return summary_dfdef load_cgpopdf():    """    load the whole centrigabor population  (n=37)    Returns    -------    pandas dataframe    """    file = 'centrigabor_pop_db.xlsx'    filename = os.path.join(paths['data'], 'averageTraces', file)    df = pd.read_excel(filename)    df.columns = [col.lower().strip() for col in df.columns]    return dfdef load_bringuier():    filename = os.path.join(paths['data'], 'bringuier.csv')    brdf = pd.read_csv(filename, sep='\t', decimal=',')    brdf.speed_upper = brdf.speed_upper ## * 1000  # 1mm / visual°    return brdf    def load_gmercier():    file = 'gmercier.csv'    filename = os.path.join(paths['data'], file)    df = pd.read_csv(filename, sep='\t')    return df# speed and baudotbddf, spdf = load_speed_data()summary_df = build_summary(bddf, spdf)# replaced cgdf by spdf (speeddf)# gmerciergmdf = load_gmercier()  # binguierbrdf = load_bringuier()brdf[['impulse', 'long_bar']] = brdf[['impulse', 'long_bar']].shift(1)# cg populationpopdf = load_cgpopdf()#%%def samebin(popdf=popdf, spdf=spdf, bddf=bddf, gmdf=gmdf, brdf=brdf):    def compute_speed_histo(speeds, bins=40):        #speeds = df.speed/1000        height, x = np.histogram(speeds, bins=bins, range=(0,1))        # normalize        # height = height/np.sum(height)        width = (max(x) - min(x))/(len(x) -1)        return x[:-1], height, width    # res dataframe 20 bins 0 to 1 range    df = pd.DataFrame(index = [_/20 for _ in range(0, 20, 1)])    # centrigabor population    x, height_cgpop, width = compute_speed_histo(popdf.speed/1000, bins=20)    df['cgpop'] = height_cgpop    # speed centrigabor pop    _, height_cgspeed, _ = compute_speed_histo(spdf.speed_isi0/1000, bins=20)    df['cgspeed'] = height_cgspeed    # baudot -> resample to double bin width    temp = bddf.set_index('optiMax')    temp.index = temp.index/1000    temp['cells'] = temp.nbCell.shift(-1).fillna(0)    temp.cells += temp.nbCell    temp = temp.drop(temp.index[1::2])    temp = temp.drop(columns=['nbCell'])    df['bd'] = temp    # gmercier    temp = gmdf.set_index('speed_high').drop(columns='speed_low')    temp.index = temp.index/1000    df['gm'] = temp    # bringuier    temp = brdf.set_index('speed_upper')    temp = temp.drop(index=[0, 100])    cols = ['br_' + st for st in temp.columns]        df[cols[0]] = temp[temp.columns[0]]    df[cols[1]] = temp[temp.columns[1]]    # fill and convert to integer    df = df.fillna(0).astype('int')    return dfbined_df = samebin()#%%def plot_optimal_speed(df):    fig = plt.figure(figsize=(11.6, 5))    ax = fig.add_subplot(111)    # plot vs uppper limit    x = (df.index - 25).to_list()    # x = summary_df.index.to_list()    height_bd = df.bd_cells.values    height_cg = df.cg_cells.values    # replace nan    height_cg = np.nan_to_num(height_cg, 0)    height_bd = np.nan_to_num(height_bd, 0)    width = 25    align='edge'    # ax.bar([_-10 for _ in x], height=height_cg, width=width, align=align,    #        color='tab:gray', edgecolor='k', label='centrigabor')    ax.bar(x, height=height_bd, width=width, align=align,           color='tab:gray', edgecolor='k', alpha = 0.6, label='baudot')    ax.bar(x, height=height_cg, bottom=height_bd, width=width, align=align,           color='w', edgecolor='k', label='centrigabor')    ax.set_xlabel('{} (°/sec)'.format('optimal apparent speed'.title()))    ax.set_ylabel('nb of Cells')    ax.legend()    for spine in ['top', 'right']:        ax.spines[spine].set_visible(False)    fig.tight_layout()    if anot:        date = datetime.now().strftime('%Y-%m-%d %H:%M:%S')        fig.text(0.99, 0.01, 'speedBaudot.py:plot_optimal_speed',                 ha='right', va='bottom', alpha=0.4)        fig.text(0.01, 0.01, date, ha='left', va='bottom', alpha=0.4)    return figplt.close('all')anot=Truefig = plot_optimal_speed(summary_df)save = Falseif save:    file = 'optSpeed.pdf'    dirname = os.path.join(paths['owncFig'], 'pythonPreview', 'baudot')    filename = os.path.join(dirname, file)    fig.savefig(filename)#%%plt.close('all')def plot_optimal_bringuier(df):    fig = plt.figure()    ax = fig.add_subplot(111)    # replace nan by O    df = df.fillna(0)    #extract & correction for lower limit#    x = (df.speed_upper - 0.05).tolist()    x = (df.speed_upper).tolist()    x = [round(_, 2) for _ in x]    height_imp = df.impulse.tolist()    height_bar = df.long_bar.tolist()    align = 'edge'    x[-1] = 1.1     # for continuous range    width =  max(x)/(len(x) -1)*.95    ax.bar(x, height=height_imp, width=width, align=align,           color='tab:green', edgecolor='k', alpha=0.8, label='impulse')    ax.bar(x, height=height_bar, width=width, align=align, alpha=1,           color='tab:blue', edgecolor='k', label='bar')    ax.set_xlabel('{} (m/s)'.format('propagation speed'.title()))    ax.set_ylabel('nb of Measures')    ax.legend()    ax.text(1.05, 0, '/ /', ha='center', va='center', backgroundcolor='w')    txt = 'n = 27 ({:.0f} measures)'.format(df.long_bar.sum())    ax.text(x=0.7, y= 0.6, s=txt, color='tab:blue',            va='bottom', ha='left', transform=ax.transAxes)    txt = 'n = 37 ({:.0f} measures)'.format(df.impulse.sum())    ax.text(x=0.7, y= 0.55, s=txt, color='tab:green',            va='bottom', ha='left', transform=ax.transAxes)    for spine in ['top', 'right']:        ax.spines[spine].set_visible(False)    fig.tight_layout()    if anot:        date = datetime.now().strftime('%Y-%m-%d %H:%M:%S')        fig.text(0.99, 0.01, 'speedBaudot.py:plot_optimal_bringuier',             ha='right', va='bottom', alpha=0.4)        fig.text(0.01, 0.01, date, ha='left', va='bottom', alpha=0.4)    return figplt.close('all')fig = plot_optimal_bringuier(brdf)save = Falseif save:    file = 'optSpreedBringuier.pdf'    dirname = os.path.join(paths['owncFig'], 'pythonPreview', 'baudot')    filename = os.path.join(dirname, file)    fig.savefig(filename)#%%# def plot_both(df0, df1, df2, df3):def plot_both(gdf = bined_df):            def compute_speed_histo(speeds, bins=40):        #speeds = df.speed/1000        height, x = np.histogram(speeds, bins=bins, range=(0,1))        # normalize        # height = height/np.sum(height)        width = (max(x) - min(x))/(len(x) -1)        return x[:-1], height, width    df = pd.DataFrame(index = [_/20 for _ in range(0, 20, 1)])    # centrigabor population    x, height_cg, width = compute_speed_histo(df0.speed/1000, bins=20)    df['cg'] = height_cg    # baudot -> resample to double bin width    x = (df1.index/1000).to_list()[::2]    cells = df1.bd_cells.to_list()    height_bd = [a + b for a,b in zip_longest(cells[0::2], cells[1::2],                                           fillvalue=0)]    bd = dict(zip(x[:-1], height_bd))    df['bd'] = pd.Series(bd)    # gmercier    df2.index = df2.index/1000    df['gm'] = pd.Series(df2['cells'])        width = (max(x) - min(x))/(len(x) - 1)    fig, axes = plt.subplots(nrows=2, ncols=1, sharex=True, figsize=(8.6, 8))    ax = axes[0]        align='edge'    df['pool'] = 0        ax.bar(df.index, df.cg, bottom=df.pool, width=width, align=align,            color='tab:red', alpha=0.6, edgecolor='k', label='centriGabor')    df.pool += df.cg    ax.bar(df.index, df.bd, bottom=df.pool, width=width, align=align,            color='tab:grey', alpha=0.6, edgecolor='k', label='baudot')    df.pool += df.bd    ax.bar(df.index, df.gm, bottom=df.pool, width=width, align=align,            color='tab:brown', alpha=0.6, edgecolor='k', label='gmercier')    df.pool += df.gm    ax.legend()        # plot vs uppper limit    # x = (df1.index - 25).to_list()    # x = [_/1000 for _ in x]    # # x = summary_df.index.to_list()    # height_bd = df1.bd_cells.values    # height_cg = df1.cg_cells.values    # # replace na    height_cg = np.nan_to_num(height_cg, 0)    # height_bd = np.nan_to_num(height_bd, 0)    align='edge'    # all centrigabor    x, height_cg, width = compute_speed_histo(df0.speed/1000, bins=20)    ax.bar(x, height_cg, width=width, align=align, color='tab:red',            alpha=0.6, edgecolor='tab:red', label='centriGabor')    # resample to double bin width    x = (summary_df.index/1000).to_list()[::2]    cells = summary_df.bd_cells.to_list()    height_bd = [a + b for a,b in zip_longest(cells[0::2], cells[1::2],                                           fillvalue=0)]    width = (max(x) - min(x))/(len(x) - 1)    ax.bar(x, height = height_bd, bottom=height_cg, width=width, align=align,           color='tab:grey', alpha=0.6, edgecolor='k', label='baudot')                # ax.bar([_-10 for _ in x], height=height_cg, width=width, align=align,    #        color='tab:gray', edgecolor='k', label='centrigabor')    ax.bar(x, height=height_cg, width=width, align=align,           color='tab:red', alpha=0.6, edgecolor='tab:red', label='optimal')    ax.bar(x, height=height_bd, bottom=height_cg, width=width, align=align,           color='tab:grey', edgecolor='tab:grey', alpha=0.5,           label='baudot')    ax.set_xlabel('{} (m/s)'.format('estimated speed'.title()))    ax.set_ylabel('nb of Cells')    ax.legend()        txt = 'n = {:.0f}'.format(df1.cg_cells.sum())    ax.text(x=0.7, y= 0.6, s=txt, color='tab:red',            va='bottom', ha='left', transform=ax.transAxes)    txt = 'n = {:.0f}'.format(df1.bd_cells.sum())    ax.text(x=0.7, y= 0.52, s=txt, color='tab:grey',            va='bottom', ha='left', transform=ax.transAxes)    for spine in ['top', 'right']:        ax.spines[spine].set_visible(False)    # ax = fig.add_subplot(212)    ax = axes[1]    # replace nan by O    df3 = df3.fillna(0)    #extract & correction for lower limit    # x = (df2.speed_upper - 0.05).tolist()    x = df3.speed_upper.tolist()    x = [round(_, 2) for _ in x]    height_imp = df3.impulse.tolist()    height_bar = df3.long_bar.tolist()    align = 'edge'    x[-1] = 1.1     # for continuous range    width = max(x)/(len(x) -1)*.95    ax.bar(x, height=height_imp, width=width, align=align,           color='tab:green', edgecolor='green', alpha=0.8, label='impulse')    ax.bar(x, height=height_bar, width=width, align=align, alpha=1,           color='tab:blue', edgecolor='blue', label='bar')    ax.set_xlabel('{} (m/s)'.format('cortical speed'.title()))    ax.set_ylabel('nb of Measures')    ax.legend()    ax.text(1.05, 0, '/ /', ha='center', va='center', backgroundcolor='w')    txt = 'n = 27 ({:.0f} measures)'.format(df3.long_bar.sum())    ax.text(x=0.7, y= 0.6, s=txt, color='tab:blue',            va='bottom', ha='left', transform=ax.transAxes)    txt = 'n = 37 ({:.0f} measures)'.format(df3.impulse.sum())    ax.text(x=0.7, y= 0.52, s=txt, color='tab:green',            va='bottom', ha='left', transform=ax.transAxes)    for spine in ['top', 'right']:        ax.spines[spine].set_visible(False)    fig.tight_layout()    # ax.set_xlim(0.1, 0.5)    # ax.set_ylim(0, 24)    lims = ax.get_xlim()    ax.set_xlim(0, lims[1])    if anot:        date = datetime.now().strftime('%Y-%m-%d %H:%M:%S')        fig.text(0.99, 0.01, 'speedBaudot.py:plot_both',                 ha='right', va='bottom', alpha=0.4)        fig.text(0.01, 0.01, date, ha='left', va='bottom', alpha=0.4)    return figplt.close('all')fig = plot_both(popdf, summary_df, gmdf, brdf)save = Falseif save:    file = 'optSpreedBoth.pdf'    dirname = os.path.join(paths['owncFig'], 'pythonPreview', 'baudot')    filename = os.path.join(dirname, file)    fig.savefig(filename)#%%# targetbins = list(np.linspace(0, 1, 21))# targetbins = [round(_, 2) for _ in targetbins]def hist_summary(brdf, summary_df, cgdf, df, gmdf, maxx=1):    def compute_speed_histo(speeds, bins=40):        #speeds = df.speed/1000        height, x = np.histogram(speeds, bins=bins, range=(0,1))        # normalize        height = height/np.sum(height)        width = (max(x) - min(x))/(len(x) -1)        return x[:-1], height, width    fig, axes = plt.subplots(figsize=(8.6,12), nrows=2, ncols=3,                             sharey=True, sharex=True)    # ax.bar(x[:-1], height, width=width, color='tab:red', alpha=0.6)    axes = axes.flatten()    # bringuier flash    ax = axes[0]    x = (brdf.speed_upper - 0.05).tolist()    x = [round(_, 2) for _ in x]    # height_imp = df2.impulse.tolist()    height_bar = (brdf.impulse)/brdf.impulse.sum().tolist()    align = 'edge'    x[-1] = 1.05     # for continuous range    width = max(x)/(len(x) -1)    # ax.bar(x, height=height_imp, width=width, align=align,    #        color='tab:green', edgecolor='green', alpha=0.8, label='impulse')    ax.bar(x, height=height_bar, width=width, align=align, alpha=1,           color='tab:green', edgecolor='k', label='impulse bringuier')    txt = 'n = 37 \n ({} measures)'.format(brdf.impulse.sum())    ax.text(x=0.6, y= 0.8, s=txt, va='top', ha='left', transform=ax.transAxes)        moy = (brdf.impulse * (brdf.speed_upper-0.025)).sum()/brdf.impulse.sum()    txt = 'mean ~ {:.2f}'.format(moy)    ax.text(x=0.7, y= 0.6, s=txt, va='top', ha='center', color='tab:green',            transform=ax.transAxes)    ax.axvline(moy, color='tab:green')    ax.legend()    # bringuier    ax = axes[1]    x = (brdf.speed_upper - 0.05).tolist()    x = [round(_, 2) for _ in x]    # height_imp = df2.impulse.tolist()    height_bar = (brdf.long_bar)/brdf.long_bar.sum().tolist()    align = 'edge'    x[-1] = 1.05     # for continuous range    width = max(x)/(len(x) -1)    # ax.bar(x, height=height_imp, width=width, align=align,    #        color='tab:green', edgecolor='green', alpha=0.8, label='impulse')    ax.bar(x, height=height_bar, width=width, align=align, alpha=1,           color='tab:blue', edgecolor='k', label='bar bringuier')    txt = 'n = 27 \n ({} measures)'.format(brdf.long_bar.sum())    ax.text(x=0.6, y= 0.8, s=txt, va='top', ha='left', transform=ax.transAxes)    moy = (brdf.long_bar * (brdf.speed_upper-0.025)).sum()/brdf.long_bar.sum()    txt = 'mean ~ {:.2f}'.format(moy)    ax.text(x=0.7, y= 0.6, s=txt, va='top', ha='center', color='tab:blue',            transform=ax.transAxes)    ax.axvline(moy, color='tab:blue')    ax.legend()            # baudot    ax = axes[2]    x = summary_df.index/1000    height = summary_df.bd_cells / summary_df.bd_cells.sum()    width = (max(x) - min(x))/(len(x) -1)    # width = 0.02    ax.bar(x, height, width=width, color='tab:purple', edgecolor='k', alpha=0.8,           label='baudot')    txt = 'n = {}'.format(int(summary_df.bd_cells.sum()))    ax.text(x=0.6, y= 0.8, s=txt, va='top', ha='left', transform=ax.transAxes)    moy = (summary_df.bd_cells * x).sum()/summary_df.bd_cells.sum()    txt = 'mean ~ {:.2f}'.format(moy)    ax.text(x=0.7, y= 0.6, s=txt, va='top', ha='center', color='tab:purple',            transform=ax.transAxes)    ax.axvline(moy, color='tab:purple')    ax.legend()        # gerard mercier    ax = axes[3]    x = gmdf.speed_low/1000    height = gmdf.cells/ gmdf.cells.sum()    width = (max(x) - min(x))/(len(x) -1)    ax.bar(x, height, width=width, color='tab:brown', edgecolor='k', align='edge',       alpha=0.8, label='gmercier')    txt = 'n = {}'.format(gmdf.cells.sum())    ax.text(x=0.6, y= 0.8, s=txt, va='top', ha='left', transform=ax.transAxes)    moy = (gmdf.cells * x).sum()/gmdf.cells.sum()    txt = 'mean ~ {:.2f}'.format(moy)    ax.text(x=0.7, y= 0.6, s=txt, va='top', ha='center', color='tab:brown',            transform=ax.transAxes)    ax.axvline(moy, color='tab:brown')    ax.legend()        # centripop    ax = axes[4]    ax.bar(*compute_speed_histo(df.speed/1000), color='tab:red', edgecolor='k',           alpha=0.8, label='centrigabor population')    txt = 'n = {}'.format(len(df))    ax.text(x=0.6, y= 0.8, s=txt, va='top', ha='left', transform=ax.transAxes)        txt = 'mean ± std : \n {:.2f} ± {:.2f}'.format(        (df.speed/1000).mean(), (df.speed/1000).std())    ax.text(x=0.7, y= 0.7, s=txt, va='top', ha='center', color='tab:red',            transform=ax.transAxes)    ax.axvline((df.speed/1000).mean(), color='tab:red')    ax.legend()    # speed pop    ax = axes[5]    ax.bar(*compute_speed_histo(cgdf.speed_isi0/1000), color='tab:orange',           edgecolor='k', alpha=0.8, label='speed population')    txt = 'n = {}'.format(len(cgdf))    ax.text(x=0.6, y= 0.8, s=txt, va='top', ha='left', transform=ax.transAxes)    txt = 'mean ± std : \n {:.2f} ± {:.2f}'.format(        (cgdf.speed_isi0/1000).mean(), (cgdf.speed_isi0/1000).std())    ax.text(x=0.7, y= 0.7, s=txt, va='top', ha='center',            color='tab:orange', transform=ax.transAxes)    ax.axvline((cgdf.speed_isi0/1000).mean(), color='tab:orange')    ax.legend()    fig.suptitle('summary for speed')    ax.set_xlim(0, maxx)    for ax in fig.get_axes():        for spine in ['left', 'top', 'right']:            ax.spines[spine].set_visible(False)            ax.tick_params(left=False, labelleft=False)    for ax in axes[2:]:        ax.set_xlabel('speed (m/s)')    if anot:        date = datetime.now().strftime('%Y-%m-%d %H:%M:%S')        fig.text(0.99, 0.01, 'speedBaudot.py:hist_summary',                 ha='right', va='bottom', alpha=0.4)        fig.text(0.01, 0.01, date, ha='left', va='bottom', alpha=0.4)    fig.tight_layout()    return figplt.close('all')fig1 = hist_summary(brdf, summary_df, spdf, popdf, gmdf)fig2 = hist_summary(brdf, summary_df, spdf, popdf, gmdf, maxx=0.5)save = Falseif save:    file = 'hist_summary.pdf'    dirname = os.path.join(paths['owncFig'], 'pythonPreview', 'baudot')    filename = os.path.join(dirname, file)    fig1.savefig(filename)    file = 'hist_summary05.pdf'    dirname = os.path.join(paths['owncFig'], 'pythonPreview', 'baudot')    filename = os.path.join(dirname, file)    fig2.savefig(filename)#%%plt.close('all')#x = gmdf.index/1000x = gmdf.speed_low/1000height = gmdf.cells/ gmdf.cells.sum()width = (max(x) - min(x))/(len(x) -1)# width = 0.02fig = plt.figure()ax = fig.add_subplot(111)ax.bar(x, height, width=width, color='tab:brown', edgecolor='k', align='edge',       alpha=0.8, label='gmercier')ax.legend()#%% build a common dataframe same bins