import sysimport osimport getpassimport pandas as pdimport matplotlib.pyplot as pltdef config():    """    to go to the pg and file directory (spyder use)    """    paths = {}    osname = sys.platform    username = getpass.getuser()    if osname == 'Windows'and username == 'Benoit':        os.chdir(r'D:\\travail\sourcecode\developing\paper\centriG')    elif osname == 'Linux' and username == 'benoit':        os.chdir(r'/media/benoit/data/travail/sourcecode/developing/paper/centriG')    elif osname == 'Windows'and username == 'marc':        os.chdir(r'H:/pg/centriG')    elif osname == 'darwin' and username == 'cdesbois':        os.chdir(r'/Users/cdesbois/pg/chrisPg/centriG')        paths['cgFig'] = os.path.expanduser('~/ownCloud/cgFigures')        paths['save'] = '/Users/cdesbois/ownCloud/cgFigures'    return pathspaths = config()#%% to be checked:sig_cells = ['1427A_CXG4',             '1429D_CXG8',             '1509E_CXG4',             '1512F_CXG6',             '1516F_CXG2',             '1516G_CXG2',             '1516M_CXG2',             '1527B_CXG2',             '1622H_CXG3',             '1638D_CXG5']#%%paths['traces'] = '/Users/cdesbois/ownCloud/cgFigures/averageTraces'info_df = pd.read_excel(os.path.join(paths['traces'], 'neuron_props.xlsx'))def load_all_vm_traces(info_df):    stims = [item for item in os.listdir(os.path.join(paths['traces'], 'vm'))             if item[0] != '.']    conds = {}    for name in stims:        filename = os.path.join(paths['traces'], 'vm', name)        df = pd.read_csv(filename, sep='\t', header=None)        df.columns = info_df.Neuron.to_list()        #center and scale        df.index -= df.index.max()/2        df.index /= 10        conds[name] = df.copy()    return condsvm_dico = load_all_vm_traces(info_df)#%%cond = 'cpisosec.txt'data = vm_dico[cond]# ref = fig = plt.figure()ax = fig.add_subplot(111)for col in data[sig_cells].columns:    ax.plot(data[col], 'r-', label=col.split('_')[0])ax.legend()